package kits.bioinfo;

import kits.bioinfo.alignment.aligner.SequenceAligner;
import kits.bioinfo.alignment.aligner.SequenceAligner.AlignmentResult;
import kits.bioinfo.alignment.scorefunction.ScoreFunction;
import kits.bioinfo.core.DnaBase;
import kits.bioinfo.core.DnaSequence;

public class Main {

	public static void main(String[] args) throws Exception {
		
		DnaSequence sequence1 = new DnaSequence("");
		DnaSequence sequence2 = new DnaSequence("");
		
		AlignmentResult<DnaBase> result = new SequenceAligner<DnaBase>(ScoreFunction.basic(1, 1, 1)).findOneGlobalAlignment(sequence1.toSequence(), sequence2.toSequence());
		System.out.println(result);
	}

}
